// Recursive function to extract all text
function extractText(item) {
  let texts = [];

  // Add snippet if it exists
  if (item.snippet && typeof item.snippet === "string") {
    texts.push(item.snippet);
  }

  // If item has a 'list' array, add each element
  if (Array.isArray(item.list)) {
    item.list.forEach(el => {
      if (typeof el === "string") {
        texts.push(el);
      } else if (typeof el === "object" && el !== null) {
        // Recursive extraction if list contains objects
        texts = texts.concat(extractText(el));
      }
    });
  }

  return texts;
}

let allTexts = [];

// Loop through each item in the workflow
for (let item of $input.first().json.text_blocks) {
  allTexts = allTexts.concat(extractText(item));
}

// Join everything into a single string (space-separated)
return [{ json: { combinedString: allTexts.join(' ') } }];
